#This script is made to help with inserting a travel time matrix table into an existing db.#TT matrix file opens using '\t' as delimiter#Example Usage#kristincarlson$ -python insertTT2DB.py -path /Users/kristincarlson/Dropbox/Bus-Highway/ParkandRide/Auto_Transit_Base_TT/PnR-Auto-TT-results.csv#     -db_param dbname=aodb-tomtom-kristin user=aodbadmin -schema tt_matrix.auto_pnr##################################           IMPORTS             ##################################import csvimport psycopg2 as pgimport argparsefrom myToolsPackage import matrixLinkModule as mod##################################           FUNCTIONS           ####################################################################           OPERATIONS          ##################################if __name__=="__main__":     # Start timing    start_time, curtime = mod.startTimer()    # Parameterize file paths    parser = argparse.ArgumentParser()    parser.add_argument('-path', '--FULL_TT_PATH', required=True, default=None) #ex. /Users/kristincarlson/Dropbox/Bus-Highway/ParkandRide/Auto_Transit_Base_TT/PnR-Auto-TT-results.csv    parser.add_argument('-db_param', '--DB_PARAMETERS', required=True, default=None) #EX. dbname=aodb-tomtom-kristin user=aodbadmin    parser.add_argument('-schema', '--SCHEMA_PARAMETERS', required=True, default=None) #EX. tt_matrix.auto_pnr    args = parser.parse_args()    #Main operations    with open('{}'.format(args.FULL_TT_PATH)) as myfile:        reader = csv.reader(myfile, delimiter='\t')        mylist=list(reader)    conn = pg.connect("{}".format(args.DB_PARAMETERS))    cur = conn.cursor()    for line in mylist:        sep=line[0].replace('','').split(',')        if sep[0]!='origin':           cur.execute("INSERT INTO {} VALUES (%s,%s,%s,%s);".format(args.SCHEMA_PARAMETERS), (sep[0],sep[1],sep[2],sep[3]))           conn.commit()           #name of db, name of table,